import "./SignUp.css";
import { useForm } from "react-hook-form";
import axios from "axios";
import { useRef, useState } from "react";
import { useNavigate } from "react-router-dom";

function SignUp() {
  let [err, setErr] = useState("");
  let navigate = useNavigate();
  let { register, handleSubmit, watch} = useForm();

  const password = watch("password", "");
  const confirmPasswordRef =  useRef()

  async function onSignUpFormSubmit(userObj) {
    const confirmPwd = confirmPasswordRef.current.value;

    if (password !== confirmPwd) {
      setErr("Passwords do not match");
      return;
    }
    
    if(userObj.userType ==='user'){
    const res = await axios.post(
      "http://localhost:3000/user-api/auth/signup",
      userObj
    );
    console.log(res);
    if (res.data.message === "User Account created successfully") {
      // navigate to sign in
      navigate("/SignIn");
    } else {
      setErr(res.data.message);
    }}
    else if(userObj.userType =='admin'){
        const res = await axios.post(
          "http://localhost:3000/admin-api/auth/signup",
          userObj
        );
        console.log(res);
        if (res.data.message === "Admin Account created successfully") {
          // navigate to sign in
          navigate("/SignIn");
        } else {
          setErr(res.data.message);
        }}
  }
  return (
    <>
      <h3>Sign Up</h3>

      {/* user register error message */}
      {err.length !== 0 && <p style={{ color: '#ff758f' }} > {err} </p>}

      <form onSubmit={handleSubmit(onSignUpFormSubmit)}>
        <input
          type="radio"
          name="userType"
          value="user"
          id="user"
          required
          {...register("userType")}
        />
        <label htmlFor="User">User</label>

        <input
          type="radio"
          name="userType"
          value="author"
          id="author"
          {...register("userType")}
        />
        <label htmlFor="Author">Author</label>
        <br />

        <label htmlFor="Email">Email</label>
        <input type="email" required {...register("email")} />
        <br />

        <label htmlFor="Username">Username</label>
        <input type="text" required {...register("username")} />
        <br />

        <label>Password</label>
        <input type="password" {...register("password", { required: true })} /> <br />

        <label>Confirm Password</label>
        <input type="password" ref={confirmPasswordRef} /> <br />

        <button type="submit">Login</button>
      </form>
    </>
  );
}

export default SignUp;
