import React, { useState, useEffect } from 'react';
import './Profile.css';

const Profile = () => {
  const [userDetails, setUserDetails] = useState({
    username: '',
    email: '',
    name: '',
    phone: '',
    address: '',
    img: null
  });

  // On mount, load user info from localStorage (you may use API instead)
  useEffect(() => {
    const storedUsername = localStorage.getItem("username") || '';
    const storedEmail = localStorage.getItem("email") || '';
    
    setUserDetails(prev => ({
      ...prev,
      username: storedUsername,
      email: storedEmail
    }));
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setUserDetails({
      ...userDetails,
      [name]: files ? files[0] : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you could send updated userDetails to the backend
    console.log('Updated Profile:', userDetails);
    alert("Profile updated successfully!");
  };

  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <form className="profile-form" onSubmit={handleSubmit}>
        <input type="text" name="username" value={userDetails.username} disabled />
        <input type="email" name="email" value={userDetails.email} disabled />
        <input type="text" name="name" value={userDetails.name} onChange={handleChange} placeholder="Full Name" />
        <input type="tel" name="phone" value={userDetails.phone} onChange={handleChange} placeholder="Phone" />
        <input type="text" name="address" value={userDetails.address} onChange={handleChange} placeholder="Address" />
        <input type="file" name="img" accept="image/*" onChange={handleChange} />
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default Profile;
