import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const AuthForm = ({ onLogin, onSignup }) => {
  const [activeTab, setActiveTab] = useState('signin');
  const [formData, setFormData] = useState({
    username: '',
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    address: ''
  });

  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    if (error) setError('');
  };

  const validateForm = () => {
    if (activeTab === 'signup') {
      if (!formData.name.trim()) return setError('Please enter your name');
      if (!formData.username.trim()) return setError('Please enter a username');
      if (!formData.phone.trim()) return setError('Please enter your phone number');
      if (!formData.address.trim()) return setError('Please enter your address');
      if (formData.password !== formData.confirmPassword) return setError('Passwords do not match');
      if (formData.password.length < 6) return setError('Password must be at least 6 characters');
    }
    if (!formData.email.includes('@')) return setError('Please enter a valid email address');
    if (!formData.password) return setError('Please enter your password');
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    if (!validateForm()) return;

    try {
      if (activeTab === 'signin') {
        const success = onLogin({
          email: formData.email,
          password: formData.password
        });

        if (success) {
          navigate('/');
        }
      } else {
        const success = onSignup({
          userType: 'user',
          username: formData.username,
          name: formData.name,
          email: formData.email,
          password: formData.password,
          phone: formData.phone,
          address: formData.address,
          cart: [],
          orders: []
        });

        if (success) {
          navigate('/');
        }
      }
    } catch (err) {
      setError(err.message || 'An error occurred during authentication');
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h1 className="auth-title">Arts Project</h1>

        {/* Tabs */}
        <div className="auth-tabs">
          <button className={`auth-tab ${activeTab === 'signin' ? 'active' : ''}`} onClick={() => setActiveTab('signin')}>Sign In</button>
          <button className={`auth-tab ${activeTab === 'signup' ? 'active' : ''}`} onClick={() => setActiveTab('signup')}>Sign Up</button>
        </div>

        {/* Error message */}
        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="form-content">
          {activeTab === 'signup' && (
            <>
              <div className="form-group">
                <label className="form-label" htmlFor="name">Full Name</label>
                <input
                  className="form-input"
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="Enter your full name"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label" htmlFor="username">Username</label>
                <input
                  className="form-input"
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  placeholder="Choose a username"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label" htmlFor="phone">Phone</label>
                <input
                  className="form-input"
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  placeholder="+1234567890"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label" htmlFor="address">Address</label>
                <input
                  className="form-input"
                  type="text"
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  placeholder="Your address"
                  required
                />
              </div>
            </>
          )}

          <div className="form-group">
            <label className="form-label" htmlFor="email">Email</label>
            <input
              className="form-input"
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="your.email@example.com"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label" htmlFor="password">Password</label>
            <input
              className="form-input"
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder="••••••••"
              required
            />
          </div>

          {activeTab === 'signup' && (
            <div className="form-group">
              <label className="form-label" htmlFor="confirmPassword">Confirm Password</label>
              <input
                className="form-input"
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                placeholder="••••••••"
                required
              />
            </div>
          )}

          {activeTab === 'signin' && (
            <div className="form-options">
              <div className="remember-me">
                <input id="remember-me" type="checkbox" className="checkbox-input" />
                <label htmlFor="remember-me" className="checkbox-label">Remember me</label>
              </div>
              <a href="#" className="forgot-password">Forgot password?</a>
            </div>
          )}

          <button type="submit" className="submit-button">
            {activeTab === 'signin' ? 'Sign In' : 'Create Account'}
          </button>
        </form>

        <div className="divider">
          <div className="divider-line"></div>
          <span className="divider-text">or continue with</span>
          <div className="divider-line"></div>
        </div>

        <div className="social-buttons">
          <button type="button" className="social-button google-login">
            <img src="./google-icon-logo-svgrepo-com.svg" alt="Google logo" className="google-logo" />
            <span>Sign in with Google</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default AuthForm;
