import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css'; // Import the CSS file

const AuthForm = ({ onLogin, onSignup }) => {
  const [activeTab, setActiveTab] = useState('signin');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: ''
  });

  localStorage.setItem("isAuthenticated", "true");
  localStorage.setItem("username", "vaishnavi123");
  localStorage.setItem("email", "vaishnavi@example.com");
  localStorage.setItem("name", "Vaishnavi");


  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    // Clear error when user starts typing
    if (error) setError('');
  };

  const validateForm = () => {
    if (activeTab === 'signup') {
      if (!formData.name.trim()) {
        setError('Please enter your name');
        return false;
      }
      if (formData.password !== formData.confirmPassword) {
        setError('Passwords do not match');
        return false;
      }
      if (formData.password.length < 6) {
        setError('Password must be at least 6 characters');
        return false;
      }
    }
    if (!formData.email.includes('@')) {
      setError('Please enter a valid email address');
      return false;
    }
    if (!formData.password) {
      setError('Please enter your password');
      return false;
    }
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    
    if (!validateForm()) return;

    try {
      if (activeTab === 'signin') {
        const success = onLogin({
          email: formData.email,
          password: formData.password
        });
        
        if (success) {
          navigate('/');
        }
      } else {
        const success = onSignup({
          name: formData.name, 
          email: formData.email,
          password: formData.password
        });
        
        if (success) {
          navigate('/');
        }
      }
    } catch (err) {
      setError(err.message || 'An error occurred during authentication');
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h1 className="auth-title">Arts Project</h1>
        
        {/* Tabs */}
        <div className="auth-tabs">
          <button
            className={`auth-tab ${activeTab === 'signin' ? 'active' : ''}`}
            onClick={() => setActiveTab('signin')}
          >
            Sign In
          </button>
          <button
            className={`auth-tab ${activeTab === 'signup' ? 'active' : ''}`}
            onClick={() => setActiveTab('signup')}
          >
            Sign Up
          </button>
        </div>
        
        {/* Error message */}
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
        
        {/* Form */}
        <form onSubmit={handleSubmit} className="form-content">
          {activeTab === 'signup' && (
            <div className="form-group">
              <label className="form-label" htmlFor="name">
                Full Name
              </label>
              <input
                className="form-input"
                type="text"
                id="name"
                name="name"
                placeholder="Enter your full name"
                value={formData.name}
                onChange={handleInputChange}
                required={activeTab === 'signup'}
              />
            </div>
          )}
          
          <div className="form-group">
            <label className="form-label" htmlFor="email">
              Email
            </label>
            <input
              className="form-input"
              type="email"
              id="email"
              name="email"
              placeholder="your.email@example.com"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label className="form-label" htmlFor="password">
              Password
            </label>
            <input
              className="form-input"
              type="password"
              id="password"
              name="password"
              placeholder="••••••••"
              value={formData.password}
              onChange={handleInputChange}
              required
            />
          </div>
          
          {activeTab === 'signup' && (
            <div className="form-group">
              <label className="form-label" htmlFor="confirmPassword">
                Confirm Password
              </label>
              <input
                className="form-input"
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                placeholder="••••••••"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                required={activeTab === 'signup'}
              />
            </div>
          )}
          
          {activeTab === 'signin' && (
            <div className="form-options">
              <div className="remember-me">
                <input 
                  id="remember-me" 
                  type="checkbox"
                  className="checkbox-input"
                />
                <label htmlFor="remember-me" className="checkbox-label">
                  Remember me
                </label>
              </div>
              <a href="#" className="forgot-password">
                Forgot password?
              </a>
            </div>
          )}
          
          <button
            type="submit"
            className="submit-button"
          >
            {activeTab === 'signin' ? 'Sign In' : 'Create Account'}
          </button>
        </form>
        
        {/* Divider */}
        <div className="divider">
          <div className="divider-line"></div>
          <span className="divider-text">or continue with</span>
          <div className="divider-line"></div>
        </div>
        
        {/* Social Login Buttons */}
        <div className="social-buttons">
          <button type="button" className="social-button google-login">
            <img
              src="./google-icon-logo-svgrepo-com.svg"
              alt="Google logo"
              className="google-logo"
            />
            <span>Sign in with Google</span>
          </button>
        </div>


      </div>
    </div>
  );
};

export default AuthForm;
