import React, { useState } from 'react';
import './Events.css'; // Create a CSS file for styling

const EventCard = ({ event }) => {
    const [showDetails, setShowDetails] = useState(false);

    const handleDetailsClick = () => {
        setShowDetails(true);
    };

    const handleClose = () => {
        setShowDetails(false);
    };

    return (
        <div className="event-card">
            <img src={event.photo} alt={event.name} className="event-photo" />
            <h3 className="event-name">{event.name}</h3>
            <button className="details-button" onClick={handleDetailsClick}>
                View Details
            </button>

            {showDetails && (
                <div className="event-popup">
                    <div className="popup-content">
                        <h2>{event.name}</h2>
                        <p>{event.details}</p>
                        <button className="close-button" onClick={handleClose}>
                            Close
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

const Events = () => {
    const events = [
        {
            id: 1,
            name: 'Event-1',
            photo: '../../',
            details: 'JNurture your childs creativity with our Drawing Class for Kids! Fun-filled lessons inspire imagination and develop fundamental drawing skills.',
        },
        {
            id: 2,
            name: 'Event-2',
            photo: '/images/Event2.avif',
            details: 'Discover your artistic potential with our pencil sketching classes for all ages. Unlock creativity and master techniques in a supportive environment.',
        },
        // Add more events as needed
    ];

    return (
        <div className="events-container">
            {events.map((event) => (
                <EventCard key={event.id} event={event} />
            ))}
        </div>
    );
};

export default Events;
